"use strict";var QUOTES="";function getQuotes(){return $.ajax({dataType:"json",url:"https://gist.githubusercontent.com/camperbot/5a022b72e96c4c9585c32bf6a75f62d9/raw/e3c6895ce42069f0ee7e991229064f167fe8ccdc/quotes.json"})}function getRandomQuote(t){return t.quotes[Math.floor(Math.random()*t.quotes.length)]}function getQuote(){var t=getRandomQuote(QUOTES);$("#text").text(t.quote),$("#author").html(t.author),$("#tweet-quote").attr("href","https://twitter.com/intent/tweet?text="+t.quote)}$("document").ready(function(){getQuotes().done(function(t){QUOTES=t}).fail(function(t){console.error("AJAX request error: "+t)}).then(function(){getQuote()}),$("#new-quote").on("click",getQuote)});